import {
  createWorkflow
} from "@medusajs/framework/workflows-sdk"
import {
  createRemoteLinkStep
} from "@medusajs/medusa/core-flows"
import {
  Modules
} from "@medusajs/framework/utils"

const helloWorldWorkflow = createWorkflow(
  "hello-world",
  () => {
    createRemoteLinkStep([{
      [Modules.PRODUCT]: {
        product_id: "prod_123",
      },
      "helloModuleService": {
        my_custom_id: "mc_123",
      },
    }])
  }
)
